import { clear } from "@testing-library/user-event/dist/clear";
import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
//import App from "./App";
import reportWebVitals from "./reportWebVitals";

const root = ReactDOM.createRoot(document.getElementById("root"));

//membuat elemen dengan function.
//Clock merupakan komponen (memakai hurup besar)
function Clock1(props) {
  return (
    <div>
      <h1>Hello World!</h1>
      <h2>Membuat elemen dengan fuction</h2>
      <h2>it is {props.date.toLocaleTimeString()}.</h2>
    </div>
  );
}

//membuat elemen dengan class
class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
    };
  }

  //lokasi yang tepat untuk mengeset interval
  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
  }
  //mengumount mengclearkan komponen yang tidak digunakan
  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date(),
    });
  }

  render() {
    return (
      <div>
        <h1>Hello World!</h1>
        <h2>Membuat elemen dengan class</h2>
        <h2>it is {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}

